import React, { useState } from "react";
import axios from "axios";

const FlightStatus = () => {
    const [flightNumber, setFlightNumber] = useState("");
    const [arrivalDate, setArrivalDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [error, setError] = useState(null);

    const fetchFlightStatus = async () => {
        try {
            setError(null); // Clear previous errors
            const response = await axios.get(
                `https://flightera-flight-data.p.rapidapi.com/flight/info`,
                {
                    params: { flnr: flightNumber, date: arrivalDate },
                    headers: {
                        "x-rapidapi-host": "flightera-flight-data.p.rapidapi.com",
                        "x-rapidapi-key": "f75c010a5dmshce36fc0fabf591dp18f1bajsn6f53f4cc2b7d",
                    },
                }
            );
            setFlightData(response.data[0]); // Assuming the first entry is relevant
        } catch (err) {
            console.error(err);
            setError("Flight not found. Please check the details and try again.");
        }
    };

    return (
        <div className="departures">
            <header>DEPARTURES</header>
            <div className="input-section">
                <input
                    type="text"
                    placeholder="Flight Number"
                    value={flightNumber}
                    onChange={(e) => setFlightNumber(e.target.value)}
                />
                <input
                    type="date"
                    value={arrivalDate}
                    onChange={(e) => setArrivalDate(e.target.value)}
                />
                <button onClick={fetchFlightStatus}>Get Status</button>
            </div>
            {error && <p className="error">{error}</p>}
            {flightData && (
                <table>
                    <thead>
                        <tr>
                            <th>Time</th>
                            <th>Destination</th>
                            <th>Flight</th>
                            <th>Gate</th>
                            <th>Remarks</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{flightData.scheduled_arrival_local}</td>
                            <td>
                                <div style={{ display: "flex", alignItems: "center" }}>
                                    <img
                                        src="flight-icon.png" // Ensure this path is correct
                                        alt="flight"
                                        style={{ width: "20px", marginRight: "10px" }}
                                    />
                                    {flightData.arrival_name}
                                </div>
                            </td>
                            <td>{flightData.flnr}</td>
                            <td>{flightData.arrival_gate}</td>
                            <td>{flightData.status}</td>
                        </tr>
                    </tbody>

                </table>
            )}
        </div>
    );
};

export default FlightStatus;
