import React, { useState } from "react";
import axios from "axios";

const FlightStatus = () => {
    const [flightNumber, setFlightNumber] = useState("");
    const [arrivalDate, setArrivalDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [error, setError] = useState(null);

    const fetchFlightStatus = async () => {
        try {
            setError(null); // Clear previous errors
            const response = await axios.get(
                `https://flightera-flight-data.p.rapidapi.com/flight/info`,
                {
                    params: { flnr: flightNumber, date: arrivalDate },
                    headers: {
                        "x-rapidapi-host": "flightera-flight-data.p.rapidapi.com",
                        "x-rapidapi-key": "f75c010a5dmshce36fc0fabf591dp18f1bajsn6f53f4cc2b7d"
                    }
                }
            );
            const flight = response.data[0]; // Assuming the first entry is relevant
            const filteredFlightData = {
                departure_name: flight.departure_name,
                arrival_name: flight.arrival_name,
                arrival_gate: flight.arrival_gate,
                scheduled_arrival_local: flight.scheduled_arrival_local,
                actual_arrival_local: flight.actual_arrival_local,
                status: flight.status
            };
            setFlightData(filteredFlightData);
        } catch (err) {
            console.error(err);
            setError("Flight not found. Please check the details and try again.");
        }
    };

    return (
        <div className="flight-status">
            <h2>Check Flight Status</h2>
            <div className="form">
                <input
                    id="flightNumber"
                    type="text"
                    placeholder="Flight Number (e.g., TG338)"
                    value={flightNumber}
                    onChange={(e) => setFlightNumber(e.target.value)}
                />
                <input
                    type="date"
                    value={arrivalDate}
                    onChange={(e) => setArrivalDate(e.target.value)}
                />
                <button onClick={fetchFlightStatus}>Get Status</button>
            </div>
            {error && <p className="error">{error}</p>}
            {/* {flightData && (
                <div className="flight-details">
                    <h3>Flight Details</h3>
                    <p><strong>Departure:</strong> {flightData.departure_name}</p>
                    <p><strong>Arrival:</strong> {flightData.arrival_name}</p>
                    <p><strong>Arrival Gate:</strong> {flightData.arrival_gate}</p>
                    <p><strong>Scheduled Arrival:</strong> {flightData.scheduled_arrival_local}</p>
                    <p><strong>Actual Arrival:</strong> {flightData.actual_arrival_local}</p>
                    <p><strong>Status:</strong> {flightData.status}</p>
                </div>
            )} */}
            <tbody>
                {flightData?.map((flight, _index) => (
                    <TableRow key={_index} flight={flight} />
                ))}
            </tbody>
        </div>
    );
};

export default FlightStatus;
