import { useState, useEffect } from 'react';
import TableRow from './TableRow';
import axios from "axios";

const FlightStatus = () => {
    const [flightNumber, setFlightNumber] = useState("");
    const [arrivalDate, setArrivalDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [error, setError] = useState(null);

    const fetchFlightStatus = async () => {
        try {
            setError(null); // Clear previous errors
            const response = await axios.get(
                `https://flightera-flight-data.p.rapidapi.com/flight/info`,
                {
                    params: { flnr: flightNumber, date: arrivalDate },
                    headers: {
                        "x-rapidapi-host": "flightera-flight-data.p.rapidapi.com",
                        "x-rapidapi-key": "f75c010a5dmshce36fc0fabf591dp18f1bajsn6f53f4cc2b7d"
                    }
                }
            );
            const flightData = response.data[0]; // Assuming the first entry is relevant
            const filteredFlightData = {
                departure_name: flightData.departure_name,
                arrival_name: flightData.arrival_name,
                arrival_gate: flightData.arrival_gate,
                scheduled_arrival_local: flightData.scheduled_arrival_local,
                actual_arrival_local: flightData.actual_arrival_local,
                status: flightData.status
            };
            setFlightData(filteredFlightData);
        } catch (err) {
            console.error(err);
            setError("Flight not found. Please check the details and try again.");
        }
    };

    return (
        <div className="flight-status">
            <h2 style={{
                position: 'relative',
                bottom: '300px',
                left: '600px',
                fontFamily: '"Lucida Handwriting", "Monotype Corsiva", cursive',
                fontSize: '30px',
                color: 'blueviolet'
            }}>Check Flight Status</h2>
            <div className="form">
                <input
                    id="flightNumber"
                    type="text"
                    placeholder="Flight Number (e.g., TG338)"
                    value={flightNumber}
                    onChange={(e) => setFlightNumber(e.target.value)}
                    style={{
                        padding: '70px 0',
                        border: 'solid',
                        textAlign: 'center',
                        display: 'flex',
                        zIndex: 3,
                        position: 'relative',
                        left: '20px',
                        bottom: '600px'
                    }}
                />
                <input
                    type="date"
                    value={arrivalDate}
                    onChange={(e) => setArrivalDate(e.target.value)}
                    style={{
                        position: 'relative',
                        bottom: '700px',
                        left: '500px'
                    }}
                />
                <button
                    onClick={fetchFlightStatus}
                    style={{
                        position: 'relative',
                        bottom: '600px',
                        left: '100px',
                        padding: '20px'
                    }}
                >
                    Get Status
                </button>
            </div>
            {error && <p className="error">{error}</p>}

            {flightData && (
                <table>
                    <tbody>
                        <TableRow flight={flightData} />
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default FlightStatus;
